import React from 'react';
import './App.css';
import profileImage from './profile.jpg';

function App() {
  return (
    <div className="App" style={{ background: '#1a1a1a', color: 'white', minHeight: '100vh', padding: '2rem' }}>
      <h2>Hi, I'm</h2>
      <h1 style={{ fontSize: '3rem', fontWeight: 'bold' }}>Darren</h1>
      <h3 style={{ fontSize: '1.5rem', marginTop: '1rem' }}>
        and I'm a <span style={{ color: '#aaa' }}>Data Engineer</span>
      </h3>

      <div style={{ marginTop: '1.5rem' }}>
        <a
          href="/Darren_CV.pdf"
          download
          style={{
            marginRight: '1rem',
            padding: '0.5rem 1rem',
            background: '#fff',
            color: '#000',
            textDecoration: 'none',
            borderRadius: '4px',
            fontWeight: 'bold'
          }}
        >
          Download CV
        </a>

        <a
          href="mailto:darren61@gmail.com"
          style={{
            padding: '0.5rem 1rem',
            background: '#333',
            color: '#fff',
            textDecoration: 'none',
            borderRadius: '4px',
            fontWeight: 'bold'
          }}
        >
          Contact Info
        </a>
      </div>

      <img
        src={profileImage}
        alt="Darren"
        style={{
          width: '250px',
          borderRadius: '50%',
          marginTop: '2rem',
          boxShadow: '0 0 25px #000'
        }}
      />
    </div>
  );
}

export default App;
